import { useState } from 'react';
import { StyleSheet, Text, View,SafeAreaView } from 'react-native';
import { TextInput, TouchableOpacity } from 'react-native-web';
const dict = require("./merged.json");
const ipa = require("./en_US.json");
export default function App() {

  const [input,onChangeInput] = useState("");
  const [output,setOutput] = useState([]);
  let results = [];
  const displayDef = ()=>{
    setOutput("");
      let originalCase= input.replace(/ +/g," ").trim();
      let words = originalCase.split(" "); //input to upper case

      for (let i=0;i<words.length;i++){
          try {
              let word,wordIpa,wordType,meanings,synonyms,antonyms;
              word = (words[i]).toUpperCase();
              wordIpa="";
              wordType = dict[word]["MEANINGS"][0][0];
              meanings = wordType?` ${dict[word]["MEANINGS"][0][1]}`:"No definition";
              synonyms = dict[word]["SYNONYMS"][0]?`Synonym: ${dict[word]["SYNONYMS"][0]}`:"";
              antonyms = dict[word]["ANTONYMS"][0]?`, Antonym: ${dict[word]["ANTONYMS"]}`:"";

              if(ipa["en_US"][0][(words[i]).toLowerCase()]){
                  wordIpa=ipa["en_US"][0][words[i]]
              }
              console.log(wordIpa)

              setOutput(output=> [...output,<Text key={i}>{<strong style={{fontSize:18}}>{words[i]} </strong> }{<p style={{fontSize:18}}>{wordIpa}</p>}{<i style={{fontSize:16}}>{wordType} </i>}{`:${meanings}(${synonyms}${antonyms}.) \n\n`}</Text>])

          } catch (error) {
              // console.log(error); 
          }
      }
  }


  return (
    <SafeAreaView>
    <View style={styles.container}>
      <View style={{flex:0.5}}>
        <Text style={styles.title}>Define words</Text>
      </View>

      <View style={{flex:2}}>
        <TextInput onChangeText ={onChangeInput} value = {input} style={styles.inputs} placeholder={"Insert your text here:"} multiline={true} numberOfLines = {20}/>
      </View>

      <View style={{flex:0.3}}>
        <TouchableOpacity onPress={displayDef} style={styles.btn}><Text style={{color:"#ffffff"}}>Check</Text></TouchableOpacity>
      </View>

      <View style={{flex:2}}>
        <Text onChangeText={setOutput} style={styles.inputs}>
          {output}
        </Text>
      </View>

    </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#ffffff',

  },
  title:{
    padding:30,
    color:"#3A6B35",
    fontSize:40
  },
  inputs:{
    padding:30,
    height:"100%",
    width:"99%",
    color:"#000000",
    backgroundColor:"#ffffff",
    fontSize:15,
    border:"0.2px solid #3A6B35"
    // opacity:0.5
  },
  btn:{
    padding:10,
    backgroundColor:"#3A6B35",
    textAlign:"center",
    justifyContent:"center",
    height:"100%",
    width:"99%"
  }
});
