export default function App() {
  const [input,onChangeInput] = useState("");
  const [output,onChangeOutput] = useState("");
  const displayDef = ()=>{
      let originalCase= input.replace(/ +/g," ").trim();
      let words = originalCase.split(" "); //input to upper case
      let results = [];
      for (let i=0;i<words.length;i++){
          try {
              let word = (words[i]).toUpperCase();
              results.push(words[i] + ": " + json[word]["MEANINGS"][0][1] + ".\n")
          } catch (error) {
              console.log(error); 
          }

      }
      onChangeOutput(results);
  }
  return (
    <SafeAreaView>
    <View style={styles.container}>
      <View style={{flex:1}}>
        <Text>Define words</Text>
      </View>
      <View style={{flex:1}}>
        <TextInput onChangeText ={onChangeInput} value = {input} style={styles.inputs} placeholder={"Insert your text here:"} multiline={true} />
      </View>
      <View style={{flex:1}}>
        <TouchableOpacity onPress={displayDef} style={styles.btn}><Text style={{color:"#ffffff"}}>Check</Text></TouchableOpacity>
      </View>
      <View style={{flex:1}}>
        <TextInput onChangeText ={onChangeOutput} value = {output} style={styles.inputs} placeholder={"The result will show here:"} disabled/>
      </View>
    </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
